describe('Integration: Error Handling', () => {
  test('ðŸ¤– AI Test: Handles invalid API key gracefully', async () => {
    try {
      await authManager.authenticate({
        provider: 'anthropic',
        apiKey: 'invalid-key'
      })
      // Should throw
      expect(true).toBe(false)
    } catch (error: any) {
      expect(error.message).toBeTruthy()
      // Error should be descriptive
      expect(error.message.length).toBeGreaterThan(10)
    }
  })

  test('ðŸ¤– AI Test: Handles unknown provider gracefully', async () => {
    try {
      await authManager.authenticate({
        provider: 'unknown-provider',
        apiKey: 'test-key'
      })
      expect(true).toBe(false)
    } catch (error: any) {
      expect(error.message).toContain('Unknown provider')
    }
  })

  test('ðŸ¤– AI Test: Can recover from failed authentication', async () => {
    // First attempt fails
    try {
      await authManager.authenticate({
        provider: 'anthropic',
        apiKey: 'invalid-key'
      })
    } catch {
      // Expected failure
    }

    // Second attempt succeeds
    const result = await authManager.authenticate({
      provider: 'anthropic',
      apiKey: testAPIKeys.anthropic
    })

    expect(result.authenticated).toBe(true)
  })

  test('ðŸ¤– AI Test: Circuit breaker tracks failures', async () => {
    // Authenticate successfully first
    await authManager.authenticate({
      provider: 'anthropic',
      apiKey: testAPIKeys.anthropic
    })

    // Check circuit breaker health
    const stats = authManager.getCircuitBreakerStats()
    expect(stats).toBeDefined()

    // Provider should be healthy initially
    const unhealthy = authManager.getUnhealthyProviders()
    expect(unhealthy.includes('anthropic')).toBe(false)
  })
})

